mapper.update:
update orders
set status=#{status},
    checkout_time=#{checkoutTime},
    pay_status=#{payStatus}
where id = #{id}

update orders
set status=#{status},
    pay_status=#{payStatus},
    rejection_reason=#{rejectReason}
where id = #{id}

mapper.delete:
update orders
set status=5,
    cancel_reason = #{cancelReason}
where id = #{id}

mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and shop_id=#{shopId}


order by order_time desc
